 */
export const fetchEcosystemApps = async ({
  timeout = 1000,
  shouldFail = false
} = {}) => {
  // Simulate API delay
  await new Promise(resolve => setTimeout(resolve, timeout));
  if (shouldFail) {
    throw new Error('Failed to fetch ecosystem apps');
  }
  return ecosystemApps;
};

/**
 * Search apps by name or description
 * @param {string} query - Search query
 * @returns {Array} Matching apps
 */
export const searchApps = query => {
  if (!query) return ecosystemApps;
  const searchTerm = query.toLowerCase();
  return ecosystemApps.filter(app => app.name.toLowerCase().includes(searchTerm) || app.description.toLowerCase().includes(searchTerm) || app.categories.some(cat => cat.toLowerCase().includes(searchTerm)));
};

/**
 * Get app statistics
 * @returns {Object} Statistics about the ecosystem
 */
export const getEcosystemStats = () => {
  return {
    totalApps: ecosystemApps.length,
    totalCategories: activeCategories.length,
    verifiedApps: ecosystemApps.filter(app => app.isVerified).length,
    categoryBreakdown: activeCategories.map(cat => ({
      name: cat.name,
      count: cat.count,
      icon: cat.icon
    }))
  };
};

// Export default object with all utilities
export default {
  apps: ecosystemApps,
  categories: activeCategories,
  fetch: fetchEcosystemApps,
  search: searchApps,
  getByCategory: getAppsByCategory,
  getStats: getEcosystemStats
};

