 * Featured apps for homepage display
 */
export const featuredApps = ecosystemApps.slice(0, 4);

/**
 * Get apps by category
 * @param {string} categoryId - Category ID to filter by
 * @returns {Array} Filtered apps
 */
export const getAppsByCategory = (categoryId) => {
  if (categoryId === 'all') return ecosystemApps;

  const category = categories.find(c => c.id === categoryId);
  if (!category) return [];

  // Map category IDs to the actual category names in the apps
  const categoryMapping = {
    'perps_derivatives': 'Perps_Derivatives',
    'dex': 'DEX',
    'lending': 'Lending_DeFi',
    'liquid_staking': 'Liquid_Staking',
    'nfts': 'NFTs_Collections',
    'gaming': 'Gaming_GameFi',
    'prediction': 'Prediction_Markets',
    'ai': 'AI_Projects',
    'infrastructure': 'Infrastructure_Tools',
    'bridges': 'Bridges_CrossChain',
    'launchpads': 'Launchpads_Meme',
    'rwa': 'RWA',
    'wallets': 'Wallets'
  };

  const targetCategory = categoryMapping[categoryId];
  if (!targetCategory) return [];

  return ecosystemApps.filter(app => app.categories.includes(targetCategory));
};

/**
 * Simulate fetching from API with error handling
 * @param {Object} options - Fetch options
 * @param {number} options.timeout - Simulated delay in ms
 * @param {boolean} options.shouldFail - Simulate API failure
 * @returns {Promise<Array>} Ecosystem apps
 */
export const fetchEcosystemApps = async ({ timeout = 1000, shouldFail = false } = {}) => {
  // Simulate API delay
  await new Promise(resolve => setTimeout(resolve, timeout));

  if (shouldFail) {
    throw new Error('Failed to fetch ecosystem apps');
  }

  return ecosystemApps;
};

/**
 * Search apps by name or description
 * @param {string} query - Search query
 * @returns {Array} Matching apps
 */
export const searchApps = (query) => {
  if (!query) return ecosystemApps;

  const searchTerm = query.toLowerCase();
  return ecosystemApps.filter(app =>
    app.name.toLowerCase().includes(searchTerm) ||
    app.description.toLowerCase().includes(searchTerm) ||
    app.categories.some(cat => cat.toLowerCase().includes(searchTerm))
  );
};

