import React from 'react';
import './AppCard.css';

const AppCard = ({ app, index }) => {
  const handleVisitApp = () => {
    window.open(app.link, '_blank', 'noopener,noreferrer');
  };

  return (
    <div 
      className="app-card animate-on-scroll"
      style={{ animationDelay: `${index * 0.1}s` }}
    >
      <div className="app-card-content">
        <div className="app-header">
          <div className="app-logo" style={{ backgroundColor: app.color + '20', borderColor: app.color }}>
            <span className="app-logo-text">{app.logo}</span>
          </div>
          <div className="app-category">
            {app.category}
          </div>
        </div>

        <div className="app-info">
          <h3 className="app-name">{app.name}</h3>
          <p className="app-description">{app.description}</p>
        </div>

        <button 
          className="app-visit-btn"
          onClick={handleVisitApp}
        >
          Visit App
          <svg viewBox="0 0 24 24" fill="none" width="16" height="16">
            <path d="M7 17L17 7M17 7H7M17 7V17" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
          </svg>
        </button>
      </div>

      <div className="app-card-glow"></div>
    </div>
  );
};

export default AppCard;
